import React, { useState, useEffect } from 'react';
import { useHistory, useParams, Link } from 'react-router-dom';
import { Layout, Menu, Table, Form, Input, Select, Button, Row, Col, Result, Divider, Card, Avatar, Statistic} from 'antd';
import { CompassTwoTone, HighlightTwoTone, EditOutlined, EllipsisOutlined, PlayCircleTwoTone, ArrowUpOutlined, ArrowDownOutlined  } from '@ant-design/icons';
import mondaySdk from "monday-sdk-js";

const { SubMenu } = Menu;
const { Option } = Select;
const { Header, Content, Footer, Sider } = Layout;
const { Meta } = Card;

const monday = mondaySdk();

const OptionSelection = () => {

    const [isSaved, setIsSaved] = useState(false)
    const [savedSearches, setSavedSearches] = useState([])
    const [isMounted, setIsMounted] = useState([])
    const [isDataLoading, setIsDataLoading] = useState(false)
    useEffect(() => {
        setIsDataLoading(true)
        // Fetch previously saved data
        setIsDataLoading(false)
        setIsMounted(true)
    }, [])

    async function loadSavedItems(){
        let resp = await monday.storage.instance.getItem('boop')
        if(!resp || !resp.data){
            return
        } else {
            if(resp.data.value !== null){
                let saved = resp.data.value;
                setSavedSearches(JSON.parse(saved))
                setIsSaved(true)
            }
        }
        // console.log(history)
        // setSearchHistory(history)
    }

    useEffect(() => {
        if(isMounted){
            console.log(savedSearches)
        }
    }, [savedSearches])

    useEffect(() => {
        if(isMounted){
            // console.log(savedSearches)
            // Do nothing
        }
    }, [isSaved])

    useEffect(() => {
        if(isMounted){
            // console.log(savedSearches)
            // Do nothing
        }
    }, [isDataLoading])


    return (
        <div className="container">
            <div className="site-statistic-demo-card">
                <Row gutter={16}>
                    <Col flex="auto">
                        <Result
                            icon={<CompassTwoTone twoToneColor="#ff3d57" />}
                            title="Let's begin with Super Search"
                            subTitle="or you can choose to run a search from the past..."
                            extra={[
                            <Link to="/search" key="0">
                                <Button type="primary" key="console">
                                    Go to Super Search
                                </Button>
                            </Link>,
                            <Link to="/view/history" key="2">
                                <Button>View Search History</Button>
                            </Link>,
                            ]}
                        />
                    </Col>
                </Row>
                <Row gutter={16} justify="center">
                    <Col flex="auto" span={20}>
                    <Divider orientation="left">
                        <h3>Saved Searches</h3>
                    </Divider>
                        <Card title="Card title">
                            <p>Card content</p>
                            <p>Card content</p>
                            <p>Card content</p>
                        </Card>
                    </Col>
                </Row>
            </div>
        </div>
    )

}

export default OptionSelection;
