import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";
import { Button, PageHeader, Input, Dropdown, Menu, Row, Col, Empty, Select, Collapse, Table } from 'antd';
import { PlayCircleFilled, DownOutlined, LoadingOutlined, SearchOutlined } from '@ant-design/icons'
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
const { Search } = Input;
const { Option } = Select;
const { Panel } = Collapse;

const qna = require("@tensorflow-models/qna")


const SearchView = () => {

    const [dataLoading, setDataLoading] = useState(false)
    const [resultsLoading, setResultsLoading] = useState(false)
    const [boardGroups, setBoardGroups] = useState([])
    const [boardColumns, setBoardColumns] = useState([])
    const [allData, setAllData] = useState({})
    const [allResults, setAllResults] = useState({})
    const [wiki, setWiki] = useState("")
    const [isMounted, setIsMounted] = useState(false)
    const [boardId, setBoardId] = useState("")
    const backButton = useHistory()

    const loadingWheel = <LoadingOutlined style={{ fontSize: 48 }} spin />;
    const groupMenu = (
        <Select
            mode="multiple"
            style={{ width: '100%' }}
            placeholder="select one country"
            defaultValue={['All Groups Selected']}
            // onChange={handleChange}
            optionLabelProp="label"
        >    
        {boardGroups.map(function (group, index){
            return (
                <Option value={group.id} label={group.title}>
                    <div className="demo-option-label-item">
                        {group.title}
                    </div>
                </Option>
            )
        })}
        </Select>
    );

    const columnMenu = (
        <Select
            mode="multiple"
            style={{ width: '100%' }}
            placeholder="select one country"
            defaultValue={['All Groups Selected']}
            // onChange={handleChange}
            optionLabelProp="label"
        >    
        {boardColumns.map(function (column, index){
            return (
                <Option value={column.id} label={column.title}>
                    <div className="demo-option-label-item">
                        {column.title}
                    </div>
                </Option>
            )
        })}
        </Select>
    );

    const dataSource = [
        {
          key: '1',
          name: 'Mike',
          age: 32,
          address: '10 Downing Street',
        },
        {
          key: '2',
          name: 'John',
          age: 42,
          address: '10 Downing Street',
        },
      ];

      const columns = [
        {
          title: 'Name',
          dataIndex: 'name',
          key: 'name',
        },
        {
          title: 'Age',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'Address',
          dataIndex: 'address',
          key: 'address',
        },
      ];

    const typeOfSearchMenu = (
        <Select defaultValue="question" style={{ width: '100%' }}>
            <Option value="question">Ask Anything</Option>
            <Option value="lucy">Match Keywords</Option>
            <Option value="Yiminghe">Regex Search</Option>
        </Select>
    )

    function clickBackButton(){
        backButton.push('/')
    }

    useEffect(() => {
        // getBoardId()
        monday.listen("context", getContext);

        setIsMounted(true)
    }, []);

    useEffect(() => {
        if(isMounted){
            getBoardGroups() // gets triggered whenever boardId changes
            // getBoardColumns() //
            // get other stuff dependent on boardId too!
        }

    }, [boardId])

    async function getContext(res){
        setBoardId(res.data.boardIds[0])
    }

    async function getBoardGroups(){
        monday.api(`query { boards(ids: ${boardId}) { columns { id title } groups { id title }} } `)
        .then((res) => {
            // setAllData(res.data)
            console.log(res.data)
            setBoardGroups(res.data.boards[0].groups)
            setBoardColumns(res.data.boards[0].columns)
        })
    }

    return (
        <div className="container">
            <PageHeader
                className="site-page-header"
                onBack={clickBackButton}
                title="Super Search"
                subTitle="Get answers from your boards!"
            />
            <div className="site-statistic-demo-card">
                <Row gutter={[16, 24]}>
                    <Col flex="auto">
                    <Input
                        placeholder="Ask a question!"
                        size="large"
                        shape="round"
                        suffix={
                            <SearchOutlined className="site-form-item-icon" />
                        }
                    />
                    </Col>
                    <Col span={4}>
                        <Button type="primary" size="large" shape="round" block>Search</Button>
                    </Col>
                </Row>
                    {boardId ?
                    <div className="container">
                     <Row gutter={[16, 24]}>
                        <Col flex="auto">
                            <Collapse defaultActiveKey={['1']} ghost>
                                <Panel header="Advanced Options" key="1">
                                    <Row gutter={16}>
                                        <Col span={8}>
                                            <label>Select groups</label>
                                            {groupMenu}
                                        </Col>
                                        <Col span={8}>
                                            <label>Select columns</label>
                                            {columnMenu}
                                        </Col>
                                        <Col span={8}>
                                            <label>Select type of search</label>
                                            {typeOfSearchMenu}
                                        </Col>
                                    </Row>
                                </Panel>
                            </Collapse>
                        </Col>
                        </Row>
                        <Row  gutter={[16, 24]}>
                            <Col flex="auto">
                                <Table dataSource={dataSource} columns={columns} />
                            </Col>
                        </Row>
                        </div>
                         :  
                         <Row gutter={[16, 24]}>

                         <Col flex="auto">
                         <Empty
                         description={
                           <span>
                             Please select a board to continue...
                           </span>
                         }
                       >
                       </Empty> 
                       </Col>
                       </Row>

                    }
            </div>
        </div>
    )

}

export default SearchView;
