import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";
import { Button, PageHeader, Input, Dropdown, Menu, Row, Col, Empty } from 'antd';
import { PlayCircleFilled, DownOutlined, SearchOutlined } from '@ant-design/icons'
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
const { Search } = Input;

const menu = (
    <Menu>
      <Menu.Item key="0">
        <a href="http://www.alipay.com/">1st menu item</a>
      </Menu.Item>
      <Menu.Item key="1">
        <a href="http://www.taobao.com/">2nd menu item</a>
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item key="3">3rd menu item</Menu.Item>
    </Menu>
  );  

const SearchView = () => {

    const [dataLoading, setDataLoading] = useState(false)
    const [resultsLoading, setResultsLoading] = useState(false)
    const [boardGroups, setBoardGroups] = useState([])
    const [allData, setAllData] = useState({})
    const [allResults, setAllResults] = useState({})
    const [wiki, setWiki] = useState("")
    const [isMounted, setIsMounted] = useState(false)
    const [boardId, setBoardId] = useState("")
    const backButton = useHistory()

    function clickBackButton(){
        backButton.push('/')
    }

    useEffect(() => {
        // getBoardId()
        monday.listen("context", getContext);

        setIsMounted(true)
    }, []);

    useEffect(() => {
        if(isMounted){
            getBoardGroups() // gets triggered whenever boardId changes
            // get other stuff dependent on boardId too!
        }

    }, [boardId])

    async function getContext(res){
        setBoardId(res.data.boardIds[0])
    }

    async function getBoardGroups(){
        monday.api(`query { boards(ids: ${boardId}) { columns { id title } groups { id title }} } `)
        .then((res) => {
            // setAllData(res.data)
            console.log(res.data)
            setBoardGroups(res.data)
        })
    }

    return (
        <div className="container">
            <PageHeader
                className="site-page-header"
                onBack={clickBackButton}
                title="Super Search"
                subTitle="Get answers from your boards!"
                // extra={[
                //     <Dropdown overlay={menu} trigger={['click']}>
                //     <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                //       Click me <DownOutlined />
                //     </a>
                //   </Dropdown>,
                //     <Button type="secondary" size="large" icon={<PlayCircleFilled />}>
                //         View Search History
                //     </Button>,
                // ]}
            />
            <div className="site-statistic-demo-card">
                <Row gutter={[16, 24]}>
                    <Col flex="auto">
                    <Input
                        placeholder="Ask a question!"
                        size="large"
                        shape="round"
                        suffix={
                            <SearchOutlined className="site-form-item-icon" />
                        }
                    />
                    </Col>
                    <Col span={4}>
                        <Button type="primary" size="large" shape="round" block>Search</Button>
                    </Col>
                </Row>
                <Row gutter={[16, 24]}>
                    {boardId ? 
                        <h1>Looks good!</h1>

                         :  
                         <Col flex="auto">
                         <Empty
                         description={
                           <span>
                             Please select a board to continue...
                           </span>
                         }
                       >
                       </Empty> 
                       </Col>
                    }
                </Row>
            </div>
        </div>
    )

}

export default SearchView;
