import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";
import { Button, PageHeader, Input, Dropdown, Menu, Row, Col, Empty, Select, Collapse, Table } from 'antd';
import { PlayCircleFilled, DownOutlined, LoadingOutlined, SearchOutlined } from '@ant-design/icons'
import '@tensorflow/tfjs-backend-webgl';
import mondaySdk from "monday-sdk-js";

const monday = mondaySdk();
const { Search } = Input;
const { Option } = Select;
const { Panel } = Collapse;

const qna = require("@tensorflow-models/qna")


const SearchView = () => {

    const [dataLoading, setDataLoading] = useState(false)
    const [resultsLoading, setResultsLoading] = useState(false)
    const [boardGroups, setBoardGroups] = useState([])
    const [boardColumns, setBoardColumns] = useState([])
    const [allData, setAllData] = useState({})
    const [allResults, setAllResults] = useState({})
    const [wiki, setWiki] = useState("")
    const [isMounted, setIsMounted] = useState(false)
    const [boardId, setBoardId] = useState("")
    const [model, setModel] = useState()
    const [question, setQuestion] = useState("")
    const [dataSource, setDataSource] = useState([])
    const [resultsColumns, setResultsColumns] = useState([])
    const backButton = useHistory()

    const loadingWheel = <LoadingOutlined style={{ fontSize: 48 }} spin />;
    const groupMenu = (
        <Select
            mode="multiple"
            style={{ width: '100%' }}
            placeholder="select one country"
            defaultValue={['All Groups Selected']}
            // onChange={handleChange}
            optionLabelProp="label"
        >    
        {boardGroups.map(function (group, index){
            return (
                <Option value={group.id} label={group.title}>
                    <div className="demo-option-label-item">
                        {group.title}
                    </div>
                </Option>
            )
        })}
        </Select>
    );

    const columnMenu = (
        <Select
            mode="multiple"
            style={{ width: '100%' }}
            placeholder="select one country"
            defaultValue={['All Groups Selected']}
            // onChange={handleChange}
            optionLabelProp="label"
        >    
        {boardColumns.map(function (column, index){
            return (
                <Option value={column.id} label={column.title}>
                    <div className="demo-option-label-item">
                        {column.title}
                    </div>
                </Option>
            )
        })}
        </Select>
    );

    const typeOfSearchMenu = (
        <Select defaultValue="question" style={{ width: '100%' }}>
            <Option value="question">Ask Anything</Option>
            <Option value="lucy">Match Keywords</Option>
            <Option value="Yiminghe">Regex Search</Option>
        </Select>
    )

    function clickBackButton(){
        backButton.push('/')
    }

    function getQuestionFromInput(e){
        setQuestion(e.target.value)
    }

    async function runSearch(){
        // create Wiki based on groups and columns selected
        console.log("Search Button was clicked")
        setResultsLoading(true)
        if(model){
            let answers = await model.findAnswers(question, wiki)
            setAllResults(answers)
        }
        // console.log(answers)
    }

    // function createWiki(){
    //     // get data based on groups and columns selected
    //     // create an index with the beginning and end of the sentences
    //     // get all the items that correspond to the search parameters

    // }

    useEffect(() => {
        if(isMounted){
            // when results change, update datasource and columns
            let newDatasource = []
            let newResultsColumns = []
            for(let i = 0; i < allResults.length; i++){
                newDatasource.push(allResults[i]);
                newDatasource[i]['key'] = i;
            }
            setDataSource(newDatasource)
            if(allResults[0]){
                let cols = Object.keys(allResults[0])
                for(let j = 0; j <= cols.length; j++){
                    newResultsColumns.push({
                        title: cols[j],
                        dataIndex: cols[j],
                        key: cols[j],
                    })
                }
            }
            setResultsColumns(newResultsColumns)
            console.log(allResults)
            setResultsLoading(false)
        }
    }, [allResults])

    useEffect(() => {
        if(isMounted){
            console.log(dataSource)
        }
    }, [dataSource])

    useEffect(() => {
        if(isMounted){
            console.log(resultsColumns)
        }
    }, [resultsColumns])

    useEffect(() => {
        // getBoardId()
        monday.listen("context", getContext);
        loadModel()
        setIsMounted(true)
    }, []);

    useEffect(() => {
        if(isMounted){
            getBoardGroups()
            getAllData()
            // gets triggered whenever boardId changes
            // getBoardColumns() //
            // get other stuff dependent on boardId too!
        }

    }, [boardId])

    useEffect(() => {
        if(isMounted){
            createWiki()
        }
    }, [allData])

    async function loadModel(){
        const runModel = await qna.load(); // importing the model
        setModel(runModel)
    }

    function createWiki(){
        let corpus = ""
        let items = allData.boards[0].items
        for(let i = 0; i < items.length; i++){
            let item = items[i]
            let item_name = items[i].name
            corpus += "This is the entry for item " + item_name + '.'
            for(let j = 0; j < item.column_values.length; j++){
                let column = item.column_values[j]
                let column_type = column.type;
                let column_text = column.text;
                if(column_type === "text"){
                    corpus += column_text;
                }
            }
        }
        setWiki(corpus)
    }

    useEffect(() => {
        console.log(wiki)
    }, [wiki])

    async function getContext(res){
        setBoardId(res.data.boardIds[0])
    }

    async function getAllData(){
        monday.api(`query {boards(ids:[${boardId}]) {items(limit:10000) {id, name, column_values {id, text, type, value, title}}}}`)
        .then((res) => {
            setAllData(res.data)
            console.log(res.data)
        })
    }

    async function getBoardGroups(){
        monday.api(`query { boards(ids: ${boardId}) { columns { id title } groups { id title }} } `)
        .then((res) => {
            // setAllData(res.data)
            console.log(res.data)
            setBoardGroups(res.data.boards[0].groups)
            setBoardColumns(res.data.boards[0].columns)
        })
    }

    return (
        <div className="container">
            <PageHeader
                className="site-page-header"
                onBack={clickBackButton}
                title="Super Search"
                subTitle="Get answers from your boards!"
            />
            <div className="site-statistic-demo-card">
                <Row gutter={[16, 24]}>
                    <Col flex="auto">
                    <Input
                        placeholder="Ask a question!"
                        size="large"
                        shape="round"
                        suffix={
                            <SearchOutlined className="site-form-item-icon" />
                        }
                        value={question}
                        onChange={getQuestionFromInput}
                    />
                    </Col>
                    <Col span={4}>
                        <Button type="primary" size="large" shape="round" block onClick={runSearch}>Search</Button>
                    </Col>
                </Row>
                    {boardId ?
                    <div className="container">
                     <Row gutter={[16, 24]}>
                        <Col flex="auto">
                            <Collapse defaultActiveKey={['1']} ghost>
                                <Panel header="Advanced Options" key="1">
                                    <Row gutter={16}>
                                        <Col span={8}>
                                            <label>Select groups</label>
                                            {groupMenu}
                                        </Col>
                                        <Col span={8}>
                                            <label>Select columns</label>
                                            {columnMenu}
                                        </Col>
                                        <Col span={8}>
                                            <label>Select type of search</label>
                                            {typeOfSearchMenu}
                                        </Col>
                                    </Row>
                                </Panel>
                            </Collapse>
                        </Col>
                        </Row>
                        <Row  gutter={[16, 24]}>
                            <Col flex="auto">
                                {resultsLoading ? <Table loading dataSource={dataSource} columns={resultsColumns} />: <Table dataSource={dataSource} columns={resultsColumns} />}

                            </Col>
                        </Row>
                        </div>
                         :  
                         <Row gutter={[16, 24]}>

                         <Col flex="auto">
                         <Empty
                         description={
                           <span>
                             Please select a board to continue...
                           </span>
                         }
                       >
                       </Empty> 
                       </Col>
                       </Row>

                    }
            </div>
        </div>
    )

}

export default SearchView;
