import React, { useState, useEffect } from 'react';
import { useHistory, useParams, Link } from 'react-router-dom';
import { Layout, Menu, Descriptions, Table, Form, Input, Select, Button, Row, Col, Result, Divider, Card, Avatar, Statistic} from 'antd';
import { CompassTwoTone, HighlightTwoTone, EditOutlined, EllipsisOutlined, PlayCircleTwoTone, ArrowUpOutlined, ArrowDownOutlined  } from '@ant-design/icons';
import mondaySdk from "monday-sdk-js";

const { SubMenu } = Menu;
const { Option } = Select;
const { Header, Content, Footer, Sider } = Layout;
const { Meta } = Card;

const monday = mondaySdk();

const OptionSelection = () => {

    return (
        <div className="container">
            <div className="site-statistic-demo-card">
                <Row gutter={16}>
                    <Col flex="auto">
                        <Result
                            icon={<CompassTwoTone twoToneColor="#ff3d57" />}
                            title="Let's begin with Super Search"
                            subTitle="or you can choose to run a search from the past..."
                            extra={[
                            <Link to="/search" key="0">
                                <Button type="primary" key="console">
                                    Go to Super Search
                                </Button>
                            </Link>,
                            <Link to="/view/history" key="2">
                                <Button>View Search History</Button>
                            </Link>,
                            ]}
                        />
                    </Col>
                </Row>
                <Row gutter={16}>
                    <Col flex="auto">
                    <Descriptions title="User Info" layout="vertical">
    <Descriptions.Item label="UserName">Zhou Maomao</Descriptions.Item>
    <Descriptions.Item label="Telephone">1810000000</Descriptions.Item>
    <Descriptions.Item label="Live">Hangzhou, Zhejiang</Descriptions.Item>
    <Descriptions.Item label="Address" span={2}>
      No. 18, Wantang Road, Xihu District, Hangzhou, Zhejiang, China
    </Descriptions.Item>
    <Descriptions.Item label="Remark">empty</Descriptions.Item>
  </Descriptions>
                    </Col>
                </Row>
            </div>
        </div>
    )

}

export default OptionSelection;
